import React,{ Fragment, useState, useEffect, useRef } from 'react';
import Breadcrumb from '../../../../../layout/breadcrumb'
import { Container, Row, Col, Card, Button, Media, CardBody } from 'reactstrap';
import BottomContainerHelper from './helpers/bottomContainer/container.js';
import Slider from 'react-slick';
import { useHistory, withRouter } from 'react-router-dom';
import { connect } from "react-redux";

const  ViewIndividualLiveStreamHelper = ({ location })  => {
    // create history obj for redirects
    const history = useHistory();
    const [ state, setState ] = useState({ nav1: null, nav2: null });
    const [ rating, setRating ] = useState(0)
    // eslint-disable-next-line

    const slider1 = useRef();
    const slider2 = useRef();

    useEffect(() => {
        setState({
            nav1: slider1.current,
            nav2: slider2.current
          });
      } , []);
    const { nav1, nav2 } = state;
    const singleItem = {
        variants: ["", "", "", "", "", "", "", ""],
        discountPrice: "450",
        price: "575",
        stock: "11"
    }
    const symbol = "$";

    const changeRating = (newRating) => {
        setRating(newRating)
    }

    return (
        <Fragment>
            <Breadcrumb parent="Ecommerce" title="Product Page"/>
            <Container fluid={true}>
                <Row>
                    <Col>
                    <Card>
                    <Row className="product-page-main">
                        <Col sm="12" md="12" lg="7" xl="7">
                            <Slider  
                                asNavFor={nav2} 
                                arrows= {false}
                                ref={slider => (slider1.current = slider)} className="product-slider">
                                    {singleItem.variants ? singleItem.variants.map((item, i) => {
                                        return (
                                            <div className="item" key={i}>
                                                <Media src={require("../../../../../assets/images/placeholder.png")} alt="" className="img-fluid" />
                                            </div>
                                        )
                                    }) : <Media src={singleItem.img} alt="" className="img-fluid" />}   
                            </Slider>
                            <Slider asNavFor={nav1}
                            ref={slider => (slider2.current= slider)}
                            slidesToShow={4}
                            swipeToSlide={true}
                            focusOnSelect={true}
                            infinite={true}
                            className="small-slick">
                                {singleItem.variants ? singleItem.variants.map((item, i) => {
                                    return (
                                        <div className="item" key={i}>
                                            <Media src={require("../../../../../assets/images/placeholder.png")} alt="banner-image" className="img-fluid" />
                                        </div>
                                    )
                                }) : ''}
                            </Slider>
                        </Col>
                        <Col sm="12" md="12" lg="5" xl="5 xl-100">
                            <Card>
                                <CardBody>

                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Card>
                <BottomContainerHelper /></Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {

    }
}
export default connect(mapStateToProps, {  })(withRouter(ViewIndividualLiveStreamHelper));
