import React, { Component, Fragment } from 'react';
import Autocomplete from "react-autocomplete";
import axios from "axios";

class LocationSearchInput extends Component {
constructor(props) {
    super(props);

    this.state = { address: '' };
}

    handleChange = e => {
        const { value } = e.target;

        this.setState({
            address: value
        }, () => {
            axios.get(`https://api.tomtom.com/search/2/autocomplete/${this.state.address}.json?key=${process.env.REACT_APP_TOMTOM_API_KEY}&language=en-US&limit=25`).then((res) => {
                console.log("Response from tomtom: ", res.data);
            }).catch((err) => {
                console.log(err);
            })
        })
    };
    render() {
        const { address } = this.state;
        return (
        <Fragment>
            <Autocomplete
                getItemValue={(item) => item.label}
                items={[
                    { label: 'apple' },
                    { label: 'banana' },
                    { label: 'pear' }
                ]}
                renderItem={(item, isHighlighted) =>
                    <div style={{ background: isHighlighted ? 'lightgray' : 'white' }}>
                        {item.label}
                    </div>
                }
                value={address}
                onChange={this.handleChange}
                onSelect={(val) => {

                }}
            />
        </Fragment>
        );
    }
}
export default LocationSearchInput;
