import React, { Fragment, Component } from 'react';
import Breadcrumb from '../../../../../../layout/breadcrumb';
import { Container, Row, Col, Card, CardHeader, Media, CardBody, Button, Form } from 'reactstrap';
import {Email,MarekjecnoMailId,BOD,DDMMYY,Designer,ContactUs,ContactUsNumber,LocationDetails,MarkJecno,Follower,Following,Location} from '../../../../../../constant';
import { withRouter } from "react-router-dom";
import axios from "axios";
import { NotificationManager } from 'react-notifications';
import Sheet from 'react-modal-sheet';
import Dropzone from 'react-dropzone-uploader';
import "./styles.css";
import ReactCrop from 'react-image-crop';
import { confirmAlert } from 'react-confirm-alert';


class PersonalProfileEmployerDetailsMainHelper extends Component {
constructor(props) {
    super(props);


    this.state = {
        aspect: 16 / 9,
        file: null
    }
}
    handleVerificationRedirect = (e) => {
        e.preventDefault();

        console.log("handle redirect verification link");

        this.props.history.push("/start/verification/flow/employer");
    }
    modifyBannerSelectedImage = () => {
        console.log("modifyBannerSelectedImage ran...!");

        // axios.post(`${process.env.REACT_APP_BASE_URL}/upload/employer/banner/photo/new`, {
        //     uniqueId: this.props.userData.uniqueId
        // }).then((res) => {
        //     if (res.data.message === "Successfully updated photo!") {
        //         console.log(res.data);

        //         NotificationManager.success(`We've successfully uploaded & updated your banner photo!`, 'Uploaded new banner photo!', 4500);
        //     } else {
        //         console.log("err", res.data);

        //         NotificationManager.error("An error occurred while uploading your new banner photo...", 'ERROR uploading new photo!', 4500);
        //     }
        // }).catch((err) => {
        //     console.log(err);
        // })
    }
    getUploadParams = ({ meta }) => { 
        return { url: 'https://httpbin.org/post' } 
    };
    handleChangeStatus = (files, allFiles) => {
        console.log("files : ", files);
        console.log("allFiles : ", allFiles);

        if (allFiles === "done") {
            this.setState({
                file: URL.createObjectURL(files.file)
            })
        }
    }
    cropCompleted = (crop, percentCrop) => {
        console.log("crop : ", crop);
        console.log("percentCrop : ", percentCrop);

        confirmAlert({
            title: 'Confirm cropped area/region',
            message: 'Are you happy with the selected region/area that was cropped or would you like to make an edit?',
            buttons: [
              {
                label: `Yes, it's fine!`,
                onClick: () => {
                    console.log("yes.");
                }
              },
              {
                label: 'No, Re-crop!',
                onClick: () => {
                    console.log("No.");
                }
              }
            ]
        });
    }
    render() {
        const { aspect, file } = this.state;
        return (
            <Fragment>
                <Sheet disableDrag={true} isOpen={this.state.isOpen} onClose={() => {
                    this.setState({
                        isOpen: false
                    })
                }}>
                    <Sheet.Container>
                    <Sheet.Header>
                        <div className="header-space-edges">
                            <Button onClick={() => {
                                this.setState({
                                    file: null,
                                    isOpen: false
                                })
                            }} style={{ width: "100%" }} color="secondary">Close/Exit Pane</Button>
                        </div>
                    </Sheet.Header>
                    <Sheet.Content>
                        <Row>
                            <Col md="12" lg="12" xl="12" sm="12">
                                <Card>
                                    <CardHeader>
                                        <h5>Upload New Banner Photo (Single-File)</h5>
                                    </CardHeader>
                                    <CardBody>
                                        <Form>
                                            <div className="dz-message needsclick">
                                                <Dropzone
                                                    getUploadParams={this.getUploadParams}
                                                    onChangeStatus={this.handleChangeStatus}
                                                    maxFiles={1}
                                                    multiple={false}
                                                    canCancel={false}
                                                    accept="image/*"
                                                    inputContent="Drop A File (ONLY Images Accepted)"
                                                    styles={{
                                                        dropzone: { height: 325 },
                                                        dropzoneActive: { borderColor: 'green' },
                                                    }}
                                                />
                                            </div>
                                        </Form>
                                        {file !== null ? <div className="lower-crop-container">
                                            <div className="lower-crop-container-inner">
                                                <p className="crop-helper-text">Please crop your image before continuing with the upload process. Note: The minimum dimensions for cropped images are 850px by 375px (Width/Height) - crops will only work when the selected region has enough room to span the entire width (left to right) of the "default crop box"</p>
                                                <hr />
                                                <ReactCrop onComplete={this.cropCompleted} imageStyle={{ border: "2px solid black" }} minWidth={850} minHeight={375} src={file} crop={aspect} onChange={newCrop => {
                                                    this.setState({
                                                        aspect: newCrop
                                                    })
                                                }} />
                                            </div>
                                        </div> : null}
                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                    </Sheet.Content>
                    </Sheet.Container>

                    <Sheet.Backdrop />
                </Sheet>
                <Breadcrumb parent="Profile data" title="Employer Profile" />
                <Container fluid={true}>
                    <div className="user-profile">
                    <Row>
                        <Col sm="12">
                        <Card className="card hovercard text-center">
                            <CardHeader className="cardheader">
                                <img src={require('../../../../../../assets/icons/edit-image.png')} onClick={() => {
                                    this.setState({
                                        isOpen: true
                                    }, () => {
                                        this.modifyBannerSelectedImage();
                                    })
                                }} className="absolute-img-top-right" />
                            </CardHeader>
                            <div className="user-image">
                            <div className="avatar"><Media body alt="" src={require('../../../../../../assets/images/user/7.jpg')} data-intro="This is Profile image" /></div>
                                <div className="icon-wrapper" data-intro="Change Profile image here">
                                    <i className="icofont icofont-pencil-alt-5">
                                    <input className="upload" type="file" />
                                    </i>
                                </div>
                            </div>
                            <div className="info">
                            <Row>
                                <Col sm="6" lg="4" className="order-sm-1 order-xl-0">
                                <Row >
                                    <Col md="6">
                                    <div className="ttl-info text-left">
                                        <h6><i className="fa fa-envelope mr-2"></i> {Email}</h6><span>{MarekjecnoMailId}</span>
                                    </div>
                                    </Col>
                                    <Col md="6">
                                    <div className="ttl-info text-left ttl-sm-mb-0">
                                        <h6><i className="fa fa-calendar"></i>{BOD}</h6><span>{DDMMYY}</span>
                                    </div>
                                    </Col>
                                </Row>
                                </Col>
                                <Col sm="12" lg="4" className="order-sm-0 order-xl-1">
                                <div className="user-designation">
                                    <div className="title"><a target="_blank" href="#javascript">{MarkJecno}</a></div>
                                    <div className="desc mt-2">{Designer}</div>
                                </div>
                                </Col>
                                <Col sm="6" lg="4" className="order-sm-2 order-xl-2">
                                <Row>
                                    <Col md="6">
                                    <div className="ttl-info text-left ttl-xs-mt">
                                        <h6><i className="fa fa-phone"></i>{ContactUs}</h6><span>{ContactUsNumber}</span>
                                    </div>
                                    </Col>
                                    <Col md="6">
                                    <div className="ttl-info text-left ttl-sm-mb-0">
                                        <h6><i className="fa fa-location-arrow"></i>{Location}</h6><span>{LocationDetails}</span>
                                    </div>
                                    </Col>
                                </Row>
                                </Col>
                            </Row>
                            <hr />
                            <div className="social-media step4" data-intro="This is your Social details">
                                <ul className="list-inline">
                                <li className="list-inline-item"><a href="#javascript"><i className="fa fa-facebook"></i></a></li>
                                <li className="list-inline-item"><a href="#javascript"><i className="fa fa-google-plus"></i></a></li>
                                <li className="list-inline-item"><a href="#javascript"><i className="fa fa-twitter"></i></a></li>
                                <li className="list-inline-item"><a href="#javascript"><i className="fa fa-instagram"></i></a></li>
                                <li className="list-inline-item"><a href="#javascript"><i className="fa fa-rss"></i></a></li>
                                </ul>
                            </div>
                            <div className="follow">
                                <Row>
                                <Col col="6" className="text-md-right border-right">
                                    <div className="follow-num counter">{"25869"}</div><span>{Follower}</span>
                                </Col>
                                <Col col="6" className="text-md-left">
                                    <div className="follow-num counter">{"659887"}</div><span>{Following}</span>
                                </Col>
                                </Row>
                            </div>
                            </div>
                        </Card>
                        </Col>
                    </Row>
                </div>
                <Row>
                    <Col sm="12" md="12" lg="4" xl="4">
                        <Card>
                            <CardHeader className="b-l-primary border-3">
                                <h5>Account Verification</h5>
                            </CardHeader>
                            <CardBody>
                                <p>Verify your account to show hackers on our platform that you're a confirmed legitimate company and safe to transact with. In order to post a job/gig, you <strong style={{ color: "blue" }}>MUST</strong> complete the verification process. Start the process by clicking the button below!</p>
                                <hr />
                                <Button style={{ width: "100%" }} onClick={this.handleVerificationRedirect} color="secondary">Start Verification!</Button>
                            </CardBody>
                        </Card>
                    </Col>
                    <Col sm="12" md="12" lg="4" xl="4">
                        <Card>
                            <CardHeader className="b-l-primary border-3">
                                <h5>Insert text here.</h5>
                            </CardHeader>
                            <CardBody>
                                <p>
                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                    {"unknown printer took a galley of type and scrambled."}
                                </p>
                            </CardBody>
                        </Card>
                    </Col>
                    <Col sm="12" md="12" lg="4" xl="4">
                        <Card>
                            <CardHeader className="b-l-primary border-3">
                                <h5>Insert text here.</h5>
                            </CardHeader>
                            <CardBody>
                                <p>
                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                    {"unknown printer took a galley of type and scrambled."}
                                </p>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
        )
    }
}

export default withRouter(PersonalProfileEmployerDetailsMainHelper);
