import React, { Fragment, useState, useEffect } from "react";
import ReactPlayer from "react-player";
import _ from "lodash";
import "./styles.css";
import { SRLWrapper } from "simple-react-lightbox";
import { Modal } from 'react-responsive-modal';

const renderPictureOrVideoLast = (file, closeLightbox, index, setSelectedModalIndex, openLightbox) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li onClick={() => {
                        setSelectedModalIndex(index);
                        // open modal and display
                        openLightbox(1);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li onClick={() => {
                        setSelectedModalIndex(index);
                        // open modal and display
                        openLightbox(1);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li onClick={() => {
                    setSelectedModalIndex(index);
                    // open modal and display
                    openLightbox(1);
                }}>
                    <div className="latest-post profile-pic-video-sidebar-custom">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
const RenderGalleryModalHackerProfileHelper = ({ onCloseModal, isOpen, user, modalIndexSelected }) => {

    const [ picturesArr, setPicturesArr ] = useState([]);
    const [ captions, setCaptions ] = useState([]);

    const options = {
        settings: {
            overlayColor: "rgba(169, 39, 249, 0.725)",
            autoplaySpeed: 2250,
            boxShadow: true
        },
        buttons: {
            backgroundColor: "#1b5245",
            iconColor: "rgba(126, 172, 139, 0.8)",
        },
        caption: {
            captionColor: "#a6cfa5",
            captionTextTransform: "uppercase",
        }
    };

    useEffect(async () => {

        if (typeof user.profilePicsVideos !== "undefined" && user.profilePicsVideos.length > 0) {
            const captionArray = [];
            // pictures exist - run setup logic
            const newlyStructuredProfilePicsVids = await user.profilePicsVideos.map((el, idx, row) => {
                if (idx + 1 === row.length) {
                    // Last one.
                    let result = {
                        src: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        caption: `You're viewing the ${el.name} file...`,
                        autoplay: false,
                        showControls: true,
                        thumbnail: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`
                    };
                    let caption = (
                        <div className="SRLCustomCaption lightbox-hacker-profile-display-custom">
                          Something - put a like or respond action here...
                          <span className="SRLCustomCaption">You're viewing {el.name}</span>
                        </div>
                    );
                    // push appropriate caption for lightbox
                    captionArray.push(caption);
                    // return constructed obj for lightbox
                    return result;
                } else {
                    // Not last one.
                    let resultNotLast = {
                        src: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        caption: `You're viewing the ${el.name} file...`,
                        autoplay: false,
                        showControls: true,
                        thumbnail: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`
                    }
                    let caption = (
                        <div className="SRLCustomCaption lightbox-hacker-profile-display-custom">
                          Something - put a like or respond action here...
                          <span className="SRLCustomCaption">You're viewing {el.name}</span>
                        </div>
                    );
                    // push appropriate caption for lightbox
                    captionArray.push(caption);
                    // return constructed obj for lightbox
                    return resultNotLast;
                }
            });
            // wait for .mapped to finish then set the local state.
            if (newlyStructuredProfilePicsVids) {
                setCaptions(captionArray);
                setPicturesArr(newlyStructuredProfilePicsVids);
            }
        }
    }, []);

    const galleryCloseModal = (data) => {
        console.log("galleryCloseModal callback :", data);

        onCloseModal(false);
    }
    return (
        <Fragment>
            <SRLWrapper options={options} customCaptions={captions}>
                {typeof picturesArr !== "undefined" && picturesArr.length > 0 ? picturesArr.map((file, idxxx) => {
                    console.log("FILE MAPPED...:", file, idxxx);
                    if (file.type === "video") {
                        return (
                            <Fragment key={idxxx}>
                                <a href={file.src} data-attribute="SRL">
                                    <video 
                                        srl_video_thumbnail={file.url}
                                        srl_video_caption={file.caption}
                                        srl_video_muted="true" 
                                        src={file.src} 
                                        alt="gallery-video" />
                                </a>
                            </Fragment>
                        );
                    } else {
                        return (
                            <Fragment key={idxxx}>
                                <a href={file.src} data-attribute="SRL">
                                    <img src={file.src} alt="gallery-pic" />
                                </a>
                            </Fragment>
                        );
                    }
                }) : null}
            </SRLWrapper>
        </Fragment>
    );
}
export default {
    renderPictureOrVideoLast,
    RenderGalleryModalHackerProfileHelper
};
