import React, { Fragment, useMemo, useCallback, useState, useEffect } from 'react';
import { useTable, useSortBy } from 'react-table'
import "./styles.css";
import Breadcrumb from '../../../../layout/breadcrumb';
import axios from "axios";

const TableHelper = ({ columns: userColumns, data, renderRowSubComponent }) => {
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        visibleColumns,
        state: { expanded }
    } = useTable(
    {
        columns: userColumns,
        data,
    },
        useSortBy
    )

    return (
    <>
        <Breadcrumb parent="View/Manage Leaderboard Data & Statistics!" title="These Are 'The Top' Ranked User's On Our Marketplace" />
        <table id={"full-stretch-table"} {...getTableProps()}>
            <thead>
            {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                    // Add the sorting props to control sorting. For this example
                    // we can add them into the header props
                    <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                    {column.render('Header')}
                    {/* Add a sort direction indicator */}
                    <span>
                        {column.isSorted
                        ? column.isSortedDesc
                            ? ' ðŸ”½'
                            : ' ðŸ”¼'
                        : ''}
                    </span>
                    </th>
                ))}
                </tr>
            ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row)
                    return (
                        // Use a React.Fragment here so the table markup is still valid
                        <React.Fragment {...row.getRowProps()}>
                            <tr>
                                {row.cells.map(cell => {
                                    return (
                                        <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                    )
                                })}
                            </tr>
                            {/*
                                If the row is in an expanded state, render a row with a
                                column that fills the entire length of the table.
                                */}
                            {row.isExpanded ? (
                                <tr>
                                <td colSpan={visibleColumns.length}>
                                    {/*
                                        Inside it, call our renderRowSubComponent function. In reality,
                                        you could pass whatever you want as props to
                                        a component like this, including the entire
                                        table instance. But for this example, we'll just
                                        pass the row
                                    */}
                                    {renderRowSubComponent({ row })}
                                </td>
                            </tr>
                        ) : null}
                        </React.Fragment>
                    )
                })}
            </tbody>
        </table>
        <br />
        <div className='text-left'>Showing the first 20 results of {rows.length} rows</div>
    </>
    )
}
