import React, { Fragment, useMemo, useCallback } from 'react';
import { useTable, useExpanded } from 'react-table'
import "./styles.css";
import Breadcrumb from '../../../../layout/breadcrumb';


const TableHelper = ({ columns: userColumns, data, renderRowSubComponent }) => {
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        visibleColumns,
        state: { expanded }
    } = useTable(
    {
        columns: userColumns,
        data,
    },
        useExpanded
    )

    return (
    <>
        <Breadcrumb parent="View/Manage Leaderboard Data & Statistics!" title="These Are 'The Top' Ranked User's On Our Marketplace" />
        <table id={"full-stretch-table"} {...getTableProps()}>
            <thead>
                {headerGroups.map(headerGroup => (
                    headerGroup.headers.map(column => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row)
                    return (
                        // Use a React.Fragment here so the table markup is still valid
                        <React.Fragment {...row.getRowProps()}>
                            <tr>
                                {row.cells.map(cell => {
                                    return (
                                        <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                    )
                                })}
                            </tr>
                            {/*
                                If the row is in an expanded state, render a row with a
                                column that fills the entire length of the table.
                                */}
                            {row.isExpanded ? (
                                <tr>
                                <td colSpan={visibleColumns.length}>
                                    {/*
                                        Inside it, call our renderRowSubComponent function. In reality,
                                        you could pass whatever you want as props to
                                        a component like this, including the entire
                                        table instance. But for this example, we'll just
                                        pass the row
                                    */}
                                    {renderRowSubComponent({ row })}
                                </td>
                            </tr>
                        ) : null}
                        </React.Fragment>
                    )
                })}
            </tbody>
        </table>
        <br />
        <div>Showing the first 20 results of {rows.length} rows</div>
    </>
    )
}


const LeaderboardHomepageHelper = (props) => {

    const columns = useMemo(() => [
        {
            Header: () => null, // No header
            id: 'expander', // It needs an ID
            Cell: ({ row }) => (
                <span {...row.getToggleRowExpandedProps()}>
                    {row.isExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}
                </span>
            ),
        },
        {
            Header: 'Name',
            columns: [
            {
                Header: 'First Name',
                accessor: 'firstName',
            },
            {
                Header: 'Last Name',
                accessor: 'lastName',
            },
            ],
        },
        {
            Header: 'Info',
            columns: [
            {
                Header: 'Age',
                accessor: 'age',
            },
            {
                Header: 'Visits',
                accessor: 'visits',
            },
            {
                Header: 'Status',
                accessor: 'status',
            },
            {
                Header: 'Profile Progress',
                accessor: 'progress',
            },
            ],
        },
        ],
        []
    )

    const data = useMemo(() => [{
        firstName: "Jeremy",
        lastName: "Blong"
    }], [])

    // Create a function that will render our row sub components
    const renderRowSubComponent = useCallback(
        ({ row }) => (
        <pre
            style={{
                fontSize: '10px',
            }}
        >
            <code>{JSON.stringify({ values: row.values }, null, 2)}</code>
        </pre>
        ),
        []
    );
    return (
        <Fragment>
            <TableHelper
                columns={columns}
                data={data}
                renderRowSubComponent={renderRowSubComponent}
            />
        </Fragment>
    );
}

export default LeaderboardHomepageHelper;
