import React, { Fragment } from "react";
import Sheet from 'react-modal-sheet';
import { Row, Col, Card, CardBody, Button, Container, CardHeader, Media, PopoverBody, PopoverHeader } from 'reactstrap';
import "./styles.css";

const ViewPostFileContentHelper = ({ isPostPaneOpen, setPostPaneOpenState }) => {
    return (
        <Fragment>
            <Sheet isOpen={isPostPaneOpen} onClose={() => setPostPaneOpenState(false)}>
                <Sheet.Container>
                <Sheet.Header>
                    <Row>
                        <Col sm="12" md="12" lg="12" xl="12">
                            <div className={"close-btn-spacer"}>
                                <Button onClick={() => setPostPaneOpenState(false)} outline className={"btn-square-danger"} color={"danger-2x"} style={{ width: "100%" }}>Close/Exit This Pane Content</Button>
                            </div>
                        </Col>
                    </Row>
                </Sheet.Header>
                    <Sheet.Content>
                        <Container fluid={true}>
                            <Row className={"main-container-post-row"}>
                                <Col sm="12" md="12" lg="12" xl="12">
                                    <Card>
                                        <CardHeader className="b-l-primary border-3">
                                            <h5>Comment, React & Participate in this post's other comment's & active participants. You can post comments & react to this post and/or comments from this current pane/slider...</h5>
                                        </CardHeader>
                                        <CardBody>
                                            <p>
                                                {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                                {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                                {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                                {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                                {"unknown printer took a galley of type and scrambled."}
                                            </p>
                                        </CardBody>
                                    </Card>
                                </Col>
                            </Row>
                        </Container>
                    </Sheet.Content>
                </Sheet.Container>
                <Sheet.Backdrop />
            </Sheet>
        </Fragment>
    );
}
export default ViewPostFileContentHelper;
