import React, { Fragment, useState, useEffect } from "react";
import Sheet from 'react-modal-sheet';
import { Row, Col, Card, CardBody, Button, Container, CardHeader, Media, PopoverBody, PopoverHeader, InputGroupAddon, Input, InputGroup, Popover } from 'reactstrap';
import "./styles.css";
import helpers from "../misc/index.js";
import moment from "moment";
import ReactPlayer from 'react-player';
import { MoreVertical } from "react-feather";
import one from '../../../../../../../assets/images/job-search/1.jpg';
import two from '../../../../../../../assets/images/job-search/6.jpg';
import timeline1 from "../../../../../../../assets/images/social-app/timeline-1.png";


const { renderProfilePicVideoPost } = helpers;

// Main function for this file is at/near the very bottom of this file... it's called <ViewPostFileContentHelper />

const renderGridImageOrVideo = (file) => {
    if (file.type.includes("video")) {
        // video logic
        return (
            <Media className={"single-post-display-video-wrapper"}>
                <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} height={"100%"} className={"maxed-out-post-video-custom"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
            </Media>
        );
    } else if (file.type.includes("image")) {
        // image logic
        return (
            <Fragment>
                <div className={"single-post-display-image-wrapper"}>
                    <Media className="single-post-display-image" alt="post-picture" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                </div>
            </Fragment>
        );
    } else {
        console.log("NEITHER MATCH.")
    }
}
const PopoverEmojiMainPostItemHelper = ({ userData, handleRespondEmoji, reactions, setPopoverState, popoverState }) => {

    const [stateCount, setStateCount] = useState(0);
    const [ready, setReady] = useState(false);

    useEffect(() => {
        let count = 0;
        // loop over reactions
        for (const key in reactions) {
            const reactionOutcome = reactions[key];

            // check if emoji is already added - if not... add it!
            if (reactionOutcome > 0) {
                count++;
            }
        }
        setStateCount(count);
        setReady(true);
    }, []);

    console.log(popoverState);

    const renderMainContentPopover = () => {
        const closePopover = () => {
            setPopoverState(false);
        }
        if (ready === true) {
            return (
                <div className={"popover-wrapper-single-post"}>
                    <Popover data-container="body" placement="top" className={"popover-restyle-individual"} isOpen={popoverState} target={"PostMainView"} toggle={() => {
                        closePopover();
                    }}>
                        <PopoverHeader className={"popover-restyle-individual-header"}>Current Post Reaction's ({stateCount} total reaction type's)</PopoverHeader>
                        <PopoverBody className={"popover-restyle-individual-body"}>
                            <div onMouseLeave={() => {
                                closePopover();
                            }} className={"mouse-exit-close-popover-emojis"}>
                                <Row>
                                    <Col className={"course-emoji-col"} sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("sunglasses", closePopover, "PostMainView")} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../assets/gifs/sunglasses.gif")} />
                                        </div>
                                    </Col>
                                    <Col className={"course-emoji-col"} sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("steaming", closePopover, "PostMainView")} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../assets/gifs/steaming.gif")} />
                                        </div>
                                    </Col>
                                    <Col className={"course-emoji-col"} sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("tearsOfJoy", closePopover, "PostMainView")} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../assets/gifs/tearsOfJoy.gif")} />
                                        </div>
                                    </Col>
                                    <Col className={"course-emoji-col"} sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("vomitting", closePopover, "PostMainView")} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../assets/gifs/vomitting.gif")} />
                                        </div>
                                    </Col>
                                    <Col className={"course-emoji-col"} sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("partying", closePopover, "PostMainView")} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../assets/gifs/partying.gif")} />
                                        </div>
                                    </Col>
                                    <Col className={"course-emoji-col"} sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("screaming", closePopover, "PostMainView")} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../assets/gifs/screaming.gif")} />
                                        </div>
                                    </Col>
                                </Row>
                            </div>
                        </PopoverBody>
                    </Popover>
                </div>
            );
        } else {
            return null;
        }
    }
    return (
        <Fragment>
            {renderMainContentPopover()}
        </Fragment>
    );
}

const ViewPostFileContentHelper = ({ selectedPost, isPostPaneOpen,  setPostPaneOpenState, user, userData }) => {

    const handleRespondEmoji = (reaction, closePopover, targetAndPopState) => {
        console.log("handleRespondEmoji clicked/run...:", reaction, closePopover, targetAndPopState);
    }

    const [ popover, setPopoverState ] = useState(false);

    console.log("POSTIEEEEEEE", selectedPost);
    return (
        <Fragment>
            <Sheet isOpen={isPostPaneOpen} onClose={() => setPostPaneOpenState(false)}>
                <Sheet.Container>
                <Sheet.Header>
                    <Row>
                        <Col sm="12" md="12" lg="12" xl="12">
                            <div className={"close-btn-spacer"}>
                                <Button onClick={() => setPostPaneOpenState(false)} outline className={"btn-square-danger"} color={"danger-2x"} style={{ width: "100%" }}>Close/Exit This Pane Content</Button>
                            </div>
                        </Col>
                    </Row>
                </Sheet.Header>
                    <Sheet.Content>
                        <Container fluid={true}>
                            <Row className={"main-container-post-row"}>
                                <Col sm="12" md="12" lg="12" xl="12">
                                    <Card>
                                        <CardHeader className="b-l-primary border-3">
                                            <h5>Comment, React & Participate in this post's other comment's & active participants. You can post comments & react to this post and/or comments from this current pane/slider...</h5>
                                        </CardHeader>
                                        <CardBody>
                                            <Card className={"add-shadow-general-card-profile"}>
                                                <CardBody>
                                                    <Row>
                                                        <Col sm="12" md="12" lg="12" xl="12">
                                                            <div className="new-users-social">
                                                                <Media>
                                                                    {renderProfilePicVideoPost(user.profilePicsVideos)}
                                                                    <Media body>
                                                                        <h6 className="mb-0 f-w-700">{`${user.firstName} ${user.lastName}`}</h6>
                                                                        <p>Posted {moment(selectedPost.date).fromNow()}</p>
                                                                    </Media><span className="pull-right mt-0"><MoreVertical /></span>
                                                                </Media>
                                                            </div>
                                                            <div style={{ paddingBottom: "12.5px" }} className="timeline-content">
                                                                <p className={"post-title-main"}>
                                                                    {selectedPost.title}
                                                                </p>
                                                            </div>
                                                        </Col>
                                                    </Row>
                                                    <Row>
                                                        <Col sm="12" md="5" lg="5" xl="5">      
                                                            {renderGridImageOrVideo(selectedPost.file)}    
                                                        </Col>
                                                        <Col sm="12" md="7" lg="7" xl="7">
                                                            <div className="new-users-social">
                                                                <Media>
                                                                    {renderProfilePicVideoPost(user.profilePicsVideos)}
                                                                    <Media body>
                                                                        <h6 className="mb-0 f-w-700">{`${user.firstName} ${user.lastName}`}</h6>
                                                                        <p>Posted {moment(selectedPost.date).fromNow()}</p>
                                                                    </Media><span className="pull-right mt-0"><MoreVertical /></span>
                                                                </Media>
                                                            </div>
                                                            <div style={{ paddingBottom: "12.5px" }} className="timeline-content">
                                                                <p className={"post-title-main"}>
                                                                    {selectedPost.title}
                                                                </p>
                                                            </div>
                                                            <hr />
                                                            <div className="timeline-content">
                                                                <p>
                                                                    {selectedPost.description}
                                                                </p>
                                                                <div className={"emoji-response-post"}>
                                                                    <img onClick={() => setPopoverState(true)} id={"PostMainView"} src={require("../../../../../../../assets/gifs/thoughts.gif")} className={"emoji-response-icon"} />
                                                                </div>
                                                                <PopoverEmojiMainPostItemHelper handleRespondEmoji={handleRespondEmoji} userData={userData} reactions={selectedPost.reactions} setPopoverState={setPopoverState} popoverState={popover} targetAndPopState={"PostMainView"} />
                                                                <div className="like-content custom-like-response-zone"><span className="pull-right comment-number"><span>{"20"} </span><span><i className="fa fa-share-alt mr-0"></i></span></span><span className="pull-right comment-number"><span>{selectedPost.comments.length} </span><span><i className="fa fa-comments-o"></i></span></span></div>
                                                                <div className="social-chat">
                                                                    {selectedPost.comments.map((comment, idxxxxxx) => {
                                                                        return (
                                                                            <Fragment key={idxxxxxx}>
                                                                                <div className="your-msg">
                                                                                    <Media>
                                                                                        <Media className="img-50 img-fluid m-r-20 rounded-circle" alt="" src={one} />
                                                                                        <Media body><span className="f-w-600">{comment.poster} <span> {moment(comment.date).fromNow()} <i className="fa fa-reply font-primary"></i></span></span>
                                                                                            <p>{comment.comment}</p>
                                                                                        </Media>
                                                                                    </Media>
                                                                                </div>
                                                                                {comment.subcomments.map((subcomment, idx) => {
                                                                                    return (
                                                                                        <Fragment key={idx}>
                                                                                            <div className="other-msg">
                                                                                                <Media>
                                                                                                    <Media className="img-50 img-fluid m-r-20 rounded-circle" alt="" src={two} />
                                                                                                    <Media body><span className="f-w-600">{subcomment.poster} <span> {moment(subcomment.date).fromNow()} <i className="fa fa-reply font-primary"></i></span></span>
                                                                                                        <p>{subcomment.comment} </p>
                                                                                                    </Media>
                                                                                                </Media>
                                                                                            </div>
                                                                                        </Fragment>
                                                                                    );
                                                                                })}
                                                                            </Fragment>
                                                                        );
                                                                    })}
                                                                    <div className="text-center"><a href={null}>More Comments</a></div>
                                                                </div>
                                                                <div className="comments-box">
                                                                    <Media>
                                                                        <Media className="img-50 img-fluid m-r-20 rounded-circle" alt="" src={one} />
                                                                        <Media body>
                                                                            <InputGroup className="text-box">
                                                                                <Input className="form-control input-txt-bx" type="text" name="message-to-send" placeholder="Post Your Comment(s)" />
                                                                                <InputGroupAddon addonType="append">
                                                                                    <Button color="transparent"><i className="fa fa-smile-o">  </i></Button>
                                                                                </InputGroupAddon>
                                                                            </InputGroup>
                                                                        </Media>
                                                                    </Media>
                                                                </div>
                                                            </div>
                                                        </Col>
                                                    </Row>
                                                </CardBody>
                                            </Card>
                                        </CardBody>
                                    </Card>
                                </Col>
                            </Row>
                        </Container>
                    </Sheet.Content>
                </Sheet.Container>
                <Sheet.Backdrop />
            </Sheet>
        </Fragment>
    );
}
export default ViewPostFileContentHelper;
