import React, { Fragment, useState, useEffect } from 'react';
import { Container, Row, Col, Card, CardHeader } from "reactstrap";
import Breadcrumb from '../../../../../layout/breadcrumb';


const ReviewEmployerAccountHelper = (props) => {
    return (
        <Fragment>
            <Breadcrumb parent="Review 'Employer' Account" title="Leave A Review For This Specific Employer!" />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className={"shadow"}>
                            <CardHeader className='b-l-info b-r-info'>
                                <h2>Leave a review for this employer account - be honest, transparent and truthful.</h2>
                                <p>We <strong>HIGHLY RECOMMEND</strong> being honest, transparent and as truthful as possible while reviewing this user. <strong>IF</strong> you feel you have a negative experience, convey that accurately but if you feel you had a <strong>positive</strong> interaction with this user, convey that accurately as well!</p>
                            </CardHeader>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}

export default ReviewEmployerAccountHelper;
