import React, { useState, useEffect, Fragment } from 'react';
import "./styles.css";
import axios from "axios";
import { connect } from "react-redux";
import { useHistory, withRouter } from "react-router-dom";
import { Container, Row, Col, Card, CardBody, CardHeader, Button, Progress, Badge } from 'reactstrap';
import ShowMoreText from "react-show-more-text";


const ViewLiveActivehiredJobsGigsDisplayHelper = ({ userData }) => {
    const [ listings, setListings ] = useState([]);
    const [ isExpanded, setExpanded ] = useState({});

    const history = useHistory();

    useEffect(() => {
        const configuration = {
            params: {
                uniqueId: userData.uniqueId,
                accountType: userData.accountType
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/active/hired/jobs/list/full`, configuration).then((res) => {
            if (res.data.message === "Successfully gathered active/hired jobs!") {
                console.log("Successfully gathered active jobs :... ", res.data);

                const { listingData } = res.data;

                let newObjCreation = {};

                for (let index = 0; index < listingData.length; index++) {
                    newObjCreation[`expanded-${index}`] = false;
                }

                setExpanded(newObjCreation);

                setListings(listingData);
            } else {
                console.log("ERROR gathering active/hired applications...:", res.data);
            }
        }).catch((err) => {
            console.log("CRITICAL ERROR gathering active/hired applications...:", err);
        })
    }, [])

    const calculateType = (type) => {
        switch (type) {
            case "both-assets":
                return "Both Digital & Physical"
                break;
            case "physical":
                return "Physical Hack ONLY";
                break;
            case "digital":
                return "Digital Hack ONLY";
                break;
            default:
                break;
        }
    }

    console.log("isExpanded state is...:", isExpanded);

    return (
        <Fragment>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card style={{ marginTop: "32.5px" }}>
                            <CardHeader className={"b-l-info"}>
                                <h1 className='cardheader-active-jobs-main'>View/Manage Your Active Hired Jobs/Gigs...</h1>
                            </CardHeader>
                            <CardBody>
                                {typeof listings !== "undefined" && listings.length > 0 ? listings.map((listing, index) => {
                                    console.log("listing", listing);

                                    const { coreEmployerData } = listing;

                                    const progress = Math.floor(Math.random() * (100 - 0 + 1) + 0);
                                    const random = Math.floor(Math.random() * (500 - 25 + 1) + 25);
                                    const employerName = `${listing.coreEmployerData.firstName} ${listing.coreEmployerData.lastName}`;
                                    const secondaryOrNot = (index % 2 === 1) ? true : false;

                                    return (
                                        <Fragment key={index}>
                                            <Col sm="12" md="4" lg="4" xl="4" className="mt-4">
                                                <div className={secondaryOrNot === true ? "project-box add-shadow-auction-wrapper-secondary" : "project-box add-shadow-auction-wrapper-info"}>
                                                    <span className={`badge ${index % 2 === 1 ? 'badge-secondary' : 'badge-info'}`}>{`You've recently been hire by the employer named ${employerName} with username of ${coreEmployerData.username}, below are your job details!`}</span>
                                                    {/* <h6 className="auction-mapped-title">{listing.originalListingData.publicCompanyName}</h6> */}
                                                    <div className="media">
                                                        <img className="img-20 mr-1 rounded-circle" src={require(`../../../../../assets/images/user/user.png`)} alt="" />
                                                    </div>
                                                    <ShowMoreText
                                                        /* Default options */
                                                        lines={4}
                                                        more="Show more"
                                                        less="Show less"
                                                        className="content-css"
                                                        anchorClass="my-anchor-css-class"
                                                        onClick={(current) => setExpanded(prevState => {
                                                            return {
                                                                ...prevState,
                                                                [`expanded-${index}`]: !current
                                                            }
                                                        })}
                                                        expanded={isExpanded[`expanded-${index}`]}
                                                        width={280}
                                                        truncatedEndingComponent={"... "}
                                                    >
                                                        <p>Submitted approach to specific task/gig: {listing.technicalApproachToHack}</p>
                                                    </ShowMoreText>
                                                    <Row className="details">
                                                        <Col xs="10" lg="10" xl="10" md="10"><span className="span-betting">Date Originally Applied </span></Col>
                                                        <Col xs="2" lg="2" xl="2" md="2" className={"spacer-col-span-auction"}><span className={secondaryOrNot === true ? 'text-secondary-custom' : 'text-info-custom'}>{listing.legibleDateApplied}</span></Col>
                                                        <Col xs="10" lg="10" xl="10" md="10"><span className="span-betting">Physical/Digital Hack:</span></Col>
                                                        <Col xs="2" lg="2" xl="2" md="2" className={"spacer-col-span-auction"}><span className={secondaryOrNot === true ? 'text-secondary-custom' : 'text-info-custom'}>{calculateType(listing.physicalOrDigitalOrBoth.value)}</span></Col>
                                                        <Col xs="10" lg="10" xl="10" md="10"> <span className="span-betting">Job Started </span></Col>
                                                        <Col xs="2" lg="2" xl="2" md="2" className={"spacer-col-span-auction"}><span className={secondaryOrNot === true ? 'text-secondary-custom' : 'text-info-custom'}>{moment(listing.date).fromNow()}</span></Col>
                                                        <Col xs="10" lg="10" xl="10" md="10"> <span className="span-betting"># People Following This Employer</span></Col>
                                                        <Col xs="2" lg="2" xl="2" md="2" className={"spacer-col-span-auction"}><span className={secondaryOrNot === true ? 'text-secondary-custom' : 'text-info-custom'}>{listing.currentlyFollowedBy.length}</span></Col>
                                                        <Col xs="10" lg="10" xl="10" md="10"> <span className="span-betting"># Of Previously Completed Jobs</span></Col>
