import React, { Fragment } from 'react';
import { CardHeader, CardBody, CardFooter, Card, Row, Col, Container } from 'reactstrap';
import "./styles.css";
import Breadcrumb from '../../../../layout/breadcrumb';

const ViewAllBalanceRelatedInfoHelper = (props) => {
    return (
        <Fragment>
            <Breadcrumb parent="View Balance (Pending, Availiable & Held)" title="View Balance & Other Related Critical Information" />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" lg="12" md="12" xl="12">
                        <Card className={"shadow-card-balance"}>
                            <CardHeader className='b-l-info b-r-info'>
                                <h3 className='balance-title'>Below you will be able to find, manage and view your related account balances & other related info!</h3>
                            </CardHeader>
                            <CardBody className='b-l-secondary b-r-secondary'>
                                <Row>
                                    <Col sm="12" lg="4" md="4" xl="4">
                                        <Card>
                                            <CardHeader className="b-l-success border-3">
                                                <h5>Availiable</h5>
                                                <p className='lead'>You currently <strong>availiable balance</strong> which you have <strong>immediate</strong> access to, these are funds you can currently withdrawl & use!</p>
                                            </CardHeader>
                                            <CardBody>
                                                <p>
                                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                                    {"unknown printer took a galley of type and scrambled."}
                                                </p>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col sm="12" lg="4" md="4" xl="4">
                                        <Card>
                                            <CardHeader className="b-l-secondary border-3">
                                                <h5>Connect Reserved</h5>
                                                <p className='lead'><strong>Funds held</strong> due to <strong>negative</strong> balances on hacker account types. These are <strong>"held"</strong> funds.</p>
                                            </CardHeader>
                                            <CardBody>
                                                <p>
                                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                                    {"unknown printer took a galley of type and scrambled."}
                                                </p>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col sm="12" lg="4" md="4" xl="4">
                                        <Card>
                                            <CardHeader className="b-l-primary border-3">
                                                <h5>Pending</h5>
                                                <p className='lead'>These funds are <strong>"pending"</strong> funds which means they are <strong>not yet</strong> availiable due to the <strong>7-day rolling pay cycle</strong>. It generally takes a few days (7) for newly aquired funds to become availiable!</p>
                                            </CardHeader>
                                            <CardBody>
                                                <p>
                                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                                    {"unknown printer took a galley of type and scrambled."}
                                                </p>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                </Row>
                            </CardBody>
                            <CardFooter className='b-l-info b-r-info'>

                            </CardFooter>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}

export default ViewAllBalanceRelatedInfoHelper;
