import React, { Fragment, useEffect, useState } from "react";
import { Popover, PopoverHeader, PopoverBody, Row, Col } from "reactstrap";
import "../styles.css";
import axios from "axios";

const RenderEmojiLogic = (reactions) => {

    const [ emojiData, emojiArrayResponded ] = useState([]);
    const [ ready, setReady ] = useState(false);

    useEffect(() => {
        const customEmojiArr = [];
        // loop over existing responses
        for (const key in reactions) {
            const element = reactions[key];
            if (element > 0) {
                customEmojiArr.push(key);
            }
        }
        emojiArrayResponded(customEmojiArr);
        setReady(true);
    }, []);

    console.log("emojiData", emojiData);

    if (ready === true) {
        return emojiData.map((reaction) => {
            if (reaction === "vomitting") {
                return <img src={require("../../../../../../../../assets/gifs/vomitting.gif")} className={"emoji-already-existant"} />;                
            } 
            // switch (reaction) {
            //     case "partying":
            //         return <img src={require("../../../../../../../../assets/gifs/partying.gif")} className={"emoji-already-existant"} />;
            //         break;
            //     case "screaming":
            //         return <img src={require("../../../../../../../../assets/gifs/screaming.gif")} className={"emoji-already-existant"} />;
            //         break;
            //     case "steaming":
            //         return <img src={require("../../../../../../../../assets/gifs/steaming.gif")} className={"emoji-already-existant"} />;
            //         break;
            //     case "sunglasses":
            //         return <img src={require("../../../../../../../../assets/gifs/sunglasses.gif")} className={"emoji-already-existant"} />;
            //         break;
            //     case "tearsOfJoy":
            //         return <img src={require("../../../../../../../../assets/gifs/tearsOfJoy.gif")} className={"emoji-already-existant"} />;
            //         break;
            //     case "vomitting":
            //         return <img src={require("../../../../../../../../assets/gifs/vomitting.gif")} className={"emoji-already-existant"} />;
            //         break;
            //     default:
            //         break;
            // }
        })
    } 
}

const RenderPopoverEmojiLogic = ({ reactions, targetAndPopState, setPopoverState, popoverState, handleRespondEmoji, comment }) => {

    const [ emojiStateArr, setEmojiStateArray ] = useState([]);
    const [ stateCount, setStateCount ] = useState(0);
    const [ ready, setReady ] = useState(false);

    useEffect(() => {

        const emojiArray = [];
        const alreadyAdded = [];
        let count = 0;

        for (const key in reactions) {
            const reactionName = key;
            const reactionOutcome = reactions[key];

            console.log("emoji looped...:", reactionName, reactionOutcome);

            // check if emoji is already added - if not... add it!
            if (reactionOutcome > 0) {
                count++;
                // push emoji into array as it doesn't exist yet
                alreadyAdded.push("reactionName");
                // create viewable history of likes/emoji responses
                const newItem = {};
                // push into array
                emojiArray.push(newItem);
            }
        }
        console.log("alreadyAdded emojis --- :", alreadyAdded);

        setStateCount(count);
        setEmojiStateArray(emojiArray);
        setReady(true);
    }, [])
    console.log("targetAndPopState :", targetAndPopState);

    console.log("custom pop check --- :", popoverState[targetAndPopState]);

    const renderMainContentPopover = () => {
        if (ready === true) {
            return (
                <Fragment>
                    <Popover placement="bottom" isOpen={popoverState[targetAndPopState]} target={targetAndPopState} toggle={() => {
                        setPopoverState(prevState => {
                            return {
                                ...prevState,
                                [targetAndPopState]: false
                            }
                        });
                    }}>
                        <PopoverHeader>Current Comment Reaction's ({stateCount} total response's)</PopoverHeader>
                        <PopoverBody>
                            <div onMouseLeave={() => {
                                setPopoverState(prevState => {
                                    return {
                                        ...prevState,
                                        [targetAndPopState]: false
                                    }
                                });
                            }} className={"mouse-exit-close-popover-emojis"}>
                                <Row>
                                    <Col sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("sunglasses", comment)} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../../assets/gifs/sunglasses.gif")} />
                                        </div>
                                    </Col>
                                    <Col sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("steaming", comment)} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../../assets/gifs/steaming.gif")} />
                                        </div>
                                    </Col>
                                    <Col sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("tearsOfJoy", comment)} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../../assets/gifs/tearsOfJoy.gif")} />
                                        </div>
                                    </Col>
                                    <Col sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("vomitting", comment)} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../../assets/gifs/vomitting.gif")} />
                                        </div>
                                    </Col>
                                    <Col sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("partying", comment)} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../../assets/gifs/partying.gif")} />
                                        </div>
                                    </Col>
                                    <Col sm="2" md="2" lg="2" xl="2">
                                        <div onClick={() => handleRespondEmoji("screaming", comment)} className={"emoji-wrapper-course"}>
                                            <img className={"custom-course-emoji-render"} src={require("../../../../../../../../assets/gifs/screaming.gif")} />
                                        </div>
                                    </Col>
                                </Row>
                            </div>
                        </PopoverBody>
                    </Popover>
                </Fragment>
            );
        } else {
            return null;
        }
    }
    // {typeof emojiStateArr !== "undefined" && emojiStateArr.length > 0 ? emojiStateArr.map((emoji, idxxx) => {
    //     console.log("custom emoji rendered...:", emoji);
    //     return (
    //         <Fragment key={idxxx}>

    //         </Fragment>
    //     );
    // }) : null}
    return (
        <Fragment>
            {renderMainContentPopover()}
        </Fragment>
    );
}

export default {
    RenderPopoverEmojiLogic,
    RenderEmojiLogic
};
