// helpers logic related to mainly to redux-hook-form
const MainHookHelpers = () => {

    const radioSelectionPricingOptions = {
        check: (setError, register, clearErrors) => {
            return (
                {...register("auctionONLY", { required: {
                    value: true,
                    message: "You MUST select an option before proceeding with this form - important bidding/purchase information is required."
                }, onBlur: (e) => {
                    // log to check whats returned
                    console.log("woah!:", e);

                    const value = e.target.value;

                    setTimeout(() => {
                        // clear error after proper selection
                        if ((typeof value !== "undefined") && (value.length > 0)) {
                            clearErrors("auctionONLY");
                        } else {
                            // set error as nothing was selected (blank 'click-off' selection of selector)
                            setError("auctionONLY", {
                                type: "manual",
                                message: "You MUST select an option before proceeding with this form - important bidding/purchase information is required.",
                            });
                        }
                    }, 50);
                }})}
            );
        },
        id: "auction-ONLY",
        name: "auctionONLY",
        value: "auction-ONLY"
    };

    return {
        radioSelectionPricingOptions
    }
}


export default MainHookHelpers;
