// helpers logic related to mainly to redux-hook-form
const HookHelpers = () => {

    const courseTitle = {
        check: (setError, register) => {
            return (
                {...register("courseTitle", { required: {
                    value: true,
                    message: "You MUST enter AT least 25 characters to 100 characters"
                }, minLength: {
                    value: 25,
                    message: "You must enter AT Least 25 characters"
                }, maxLength: {
                    value: 100,
                    message: "You may ONLY enter 100 characters or less"
                }, onBlur: (e) => {
                    // code here...
                }})}
            )
        },
        onChange: (e, setValue) => {
            const value = e.target.value;

            setValue("courseTitle", value, { shouldValidate: true });
        },
        name: "courseTitle",
        placeholder: "Enter a 'Course Title'",
        type: "text",
        label: "Course Title"
    };
    const courseCategory = {
        check: (setError, register, values) => {
            return (
                {...register("reservePrice", { required: {
                    value: true,
                    message: "You MUST enter AT least 1 characters to 10 characters"
                }, minLength: {
                    value: 1,
                    message: "You must enter AT Least 1 characters"
                }, maxLength: {
                    value: 10,
                    message: "You may ONLY enter 10 characters or less"
                }, onBlur: (e) => {
                    // code here...
                    console.log("blur - e", e);
                    // current value from this input
                    const current = e.target.value;
                    // starting bid
                    const startingBid = values.startBid;
                    // compare to make sure reserve is greater than starting bid
                    if (Number(current) <= Number(startingBid)) {
                        setTimeout(() => {
                            setError("reservePrice", {
                                type: "manual",
                                message: "You MUST enter a 'reserve price' GREATER than your starting bid price!",
                            });
                        }, 50);
                    }
                }})}
            )
        },
        name: "reservePrice",
        placeholder: "Enter a reserve price...",
        type: "number",
        label: "Enter a reserve price"
    };

    return {
        courseTitle,
        courseCategory
    }
}


export default HookHelpers;
