import React, { Fragment, useState, useEffect } from 'react';
import "./styles.css";
import { Container, Row, Col, Card, CardHeader, CardBody, Button } from "reactstrap";
import Breadcrumb from '../../../../../layout/breadcrumb';
import axios from "axios";
import { connect } from "react-redux";
import { useHistory } from 'react-router-dom'
import Ratings from 'react-ratings-declarative'
import moment from "moment";

const ManageVariousVideoCallsHelper = ({ userData }) => {

    const history = useHistory();


    const [ pending, setPending ] = useState(false);

    useEffect(() => {
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/general/user/data`, {
            params: {
                id: userData.uniqueId,
                accountType: userData.accountType
            }
        }).then((res) => {
            if (res.data.message === "Gathered user!") {
                console.log(res.data);

                const { user } = res.data;

                setPending(user.pendingVideoCalls);
            } else {
                console.log("Err", res.data);
            }
        }).catch((err) => {
            console.log("critical err", err);
        })
    }, []); 


    return (
        <Fragment>
            <Breadcrumb parent="Manage Your Video Related Calling Data!" title="Video Interview Invite's, Cancel/Manage Pending Calls & More.."/>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className={"shadow"}>
                            <CardHeader className={"b-l-info b-r-info"}>

                            </CardHeader>
                            <CardBody>
                                <Row>
                                    {typeof pending !== "undefined" && pending.length > 0 ? pending.map((item, index) => {
                                        return (
                                            <Fragment>
                                                <Col sm="12" md="6" lg="6" xl="6">
                                                    <Card>
                                                        <CardBody>
                                                            <div className="product-page-details">
                                                                <h3>Scheduled for <em style={{ textDecorationLine: "underline" }}>{moment(item.date_picked).format("MM/DD/YYYY")}</em></h3>
                                                            </div>
                                                            <div className="product-price f-28">
                                                                Exact Time: <em style={{ textDecorationLine: "underline" }}>{item.time.label}</em>
                                                            </div>
                                                            <ul className="product-color m-t-15">
                                                                <li className="bg-primary"></li>
                                                                <li className="bg-secondary"></li>
                                                                <li className="bg-success"></li>
                                                                <li className="bg-info"></li>
                                                                <li className="bg-warning"></li>
                                                            </ul>
                                                            <hr/>
                                                            <p>{"It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that."}</p>
                                                            <hr/>
                                                            <div>
                                                                <table className="product-page-width">
                                                                    <tbody>
                                                                        <tr>
                                                                        <td> <b>Brand &nbsp;&nbsp;&nbsp;:</b></td>
                                                                        <td>{"Pixelstrap"}</td>
                                                                        </tr>
                                                                        <tr>
                                                                        <td> <b>Availability &nbsp;&nbsp;&nbsp;: &nbsp;&nbsp;&nbsp;</b></td>
                                                                        <td className="txt-success">5</td>
                                                                        </tr>
                                                                        <tr>
                                                                        <td> <b>{"Seller"} &nbsp;&nbsp;&nbsp;: &nbsp;&nbsp;&nbsp;</b></td>
                                                                        <td>{"ABC"}</td>
                                                                        </tr>
                                                                        <tr>
                                                                        <td> <b>{"Fabric"} &nbsp;&nbsp;&nbsp;: &nbsp;&nbsp;&nbsp;</b></td>
                                                                        <td>{"Cotton"}</td>
                                                                        </tr>
                                                                    </tbody>
                                                                </table>
                                                            </div>
                                                            <hr/>
                                                            <Row>
                                                                <Col md="6">
                                                                    <h6 className="product-title">{"Created:"}</h6>
                                                                </Col>
                                                                <Col md="6">
                                                                    <h6 className="product-title">{moment(item.date).fromNow()}</h6>
                                                                </Col>
                                                            </Row>
                                                            <hr/>
                                                            <Row>
                                                                <Col md="6">
                                                                <h6 className="product-title">{"Rate Now"}</h6>
                                                                </Col>
                                                                <Col md="6">
                                                                    <div className="d-flex">
                                                                            <Ratings
                                                                            rating={5}
                                                                            widgetRatedColors="blue"
                                                                            changeRating={null}
                                                                            >
                                                                            <Ratings.Widget />
                                                                            <Ratings.Widget />
                                                                            <Ratings.Widget />
                                                                            <Ratings.Widget />
                                                                            <Ratings.Widget />
                                                                        </Ratings>
                                                                        <span>Product Review</span>
                                                                    </div>
                                                                </Col>
                                                            </Row>
                                                            <hr/>
                                                            <div className="m-t-15">
                                                                <Button color="success-2x" outline style={{ width: "49.5%" }} className="m-r-10" onClick={() => {}}>
                                                                    <i className="fa fa-solid fa-video mr-1"></i>View/Activate Video Call
                                                                </Button>
                                                                <Button color="danger-2x" outline style={{ width: "49.5%" }} className="m-r-10" onClick={() => {}}>
                                                                    <i className="fa fa-solid fa-ban mr-1"></i>Cancel/Modify Call
                                                                </Button>
                                                            </div>
                                                        </CardBody>
                                                    </Card>
                                                </Col> 
                                            </Fragment>
                                        );
                                    }) : null}
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, { })(ManageVariousVideoCallsHelper);
