import React, { Fragment, useState, useEffect } from 'react';
import { Container, Row, Col, Card, CardBody, CardHeader, Media, TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";
import Breadcrumb from '../../../../../layout/breadcrumb';
import "./styles.css";
import { useParams } from "react-router-dom";
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton';
import axios from "axios";
import TimelineTabEmployerProfileHelper from './helpers/timelineTab.js';
import PhotosTabEmployerProfileHelper from './helpers/aboutTab.js';
import FriendsTabEmployerProfileHelper from './helpers/friendsTab.js';
import PhotosTab from './helpers/photosTab.js';
import _ from "lodash";
import helpers from "./helpers/miscFunctions/helperFunctions.js";


const { renderProfilePicVideo } = helpers;

const MainEmployerProfileDisplayHelper = (props) => {

    const [ activeTab, setActiveTab ] = useState('1');

    const [ employerData, setEmployerData ] = useState(null);

    const { id } = useParams();

    useEffect(() => {
        const configuration = {
            params: {
                uniqueId: id
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/retrieve/related/employer/core/information`, configuration).then((res) => {
            if (res.data.message === "Gathered relevant information!") {
                console.log(res.data);

                const { user } = res.data;

                setEmployerData(user);
            } else {
                console.log("errr inside...:", res.data);
            }
        }).catch((err) => {
            console.log(err);
        })
    }, [])

    const renderContentMain = () => {
        return (
            <Fragment>
                <Container fluid={true}>
                    <div className="user-profile social-app-profile">
                        <Row>
                            <Col sm="12">
                                <Card style={{ paddingTop: "27.5px" }} className="hovercard text-center">
                                    <div className="cardheader socialheader">
                                        {_.has(employerData, "profileBannerImage") ? <img src={`${process.env.REACT_APP_ASSET_LINK}/${employerData.profileBannerImage.link}`} className={"stretch-banner-profile-picture"} /> : null}
                                    </div>
                                    <div className="user-image">
                                        <div className="avatar">
                                            {renderProfilePicVideo(_.has(employerData, "profilePicsVideos") && typeof employerData.profilePicsVideos !== "undefined" && employerData.profilePicsVideos.length > 0 ? employerData.profilePicsVideos[employerData.profilePicsVideos.length - 1] : null)} />
                                        </div>
                                        <ul className="share-icons">
                                            <li><a className="social-icon bg-primary" href={null}><i className="fa fa-smile-o"></i></a></li>
                                            <li><a className="social-icon bg-secondary" href={null}><i className="fa fa-wechat"></i></a></li>
                                            <li><a className="social-icon bg-warning" href={null}><i className="fa fa-share-alt"></i></a></li>
                                        </ul>
                                    </div>
                                    <div className="info market-tabs p-0">
                                        <Nav tabs className="border-tab tabs-scoial">
                                            <NavItem className="nav" id="myTab" role="tablist">
                                                <NavLink tag="a" href={null} className={activeTab === '1' ? 'active' : ''} onClick={() => setActiveTab('1')}>
                                                    Timeline
                                            </NavLink>
                                            </NavItem>
                                            <NavItem className="nav" id="myTab" role="tablist">
                                                <NavLink tag="a" href={null} className={activeTab === '2' ? 'active' : ''} onClick={() => setActiveTab('2')}>
                                                    About
                                            </NavLink>
                                            </NavItem>
                                            <NavItem>
                                                <div className="user-designation"></div>
                                                <div className="title"><a target="_blank" href={null}>{"ElANA"}</a></div>
                                                <div className="desc mt-2">General Manager</div>
                                            </NavItem>
                                            <NavItem className="nav" id="myTab" role="tablist">
                                                <NavLink tag="a" href={null} className={activeTab === '3' ? 'active' : ''} onClick={() => setActiveTab('3')}>
                                                    Friend's
                                            </NavLink>
                                            </NavItem>
                                            <NavItem className="nav" id="myTab" role="tablist">
                                                <NavLink tag="a" href={null} className={activeTab === '4' ? 'active' : ''} onClick={() => setActiveTab('4')}>
                                                    Photo's
                                            </NavLink>
                                            </NavItem>
                                        </Nav>
                                    </div>
                                </Card>
                            </Col>
                        </Row>
                        <TabContent activeTab={activeTab} className="tab-content">
                            <TabPane tabId="1">
                                <TimelineTabEmployerProfileHelper />
                            </TabPane>
                            <TabPane tabId="2">
                                <PhotosTabEmployerProfileHelper />
                            </TabPane>
                            <TabPane tabId="3">
                                <FriendsTabEmployerProfileHelper />
                            </TabPane>
                            <TabPane tabId="4">
                                <PhotosTab />
                            </TabPane>
                        </TabContent>
                    </div>
                </Container>
            </Fragment>
        );
    }

    return (
        <Fragment>
           <Breadcrumb parent="Viewing Individual Employer Profile Page" title="You're Now Viewing This Employer's Profile Information/Data" />
            <Container fluid={true}>
                <Row>
                    <Card className='b-l-info b-r-info shadow-wrapper-overview-profile-employer'>
                        {employerData !== null ? renderContentMain() : <Fragment>
                            <SkeletonTheme baseColor="#c9c9c9" highlightColor="#444">
                                <p>
                                    <Skeleton count={50} />
                                </p>
                            </SkeletonTheme>
                        </Fragment>}
                    </Card>
                </Row>
            </Container>
        </Fragment>
    );
}

export default MainEmployerProfileDisplayHelper;
