const TimelinePostsMappedHelper = ({ post, key, user, userData, setPopoverState, popover, popoverIDTarget }) => {
    switch (post.uploadedRelevantFiles.length) {
        case 1:
            return (
                <Fragment key={key}>
                    <Col sm="12">
                        <Card>
                            <CardBody>
                                <div className="new-users-social">
