import React, { useState, useEffect, useRef, Fragment } from 'react';
import "./styles.css";
import { Container, Row, Col, Card, CardHeader, CardBody, Form } from 'reactstrap'
import Dropzone from 'react-dropzone-uploader';


const PageTwoMainHelper = (props) => {

    const getUploadParams = ({ meta }) => { 
        return { url: 'https://httpbin.org/post' } 
    };


    const handleChangeStatus = ({ meta, file }, status) => {
        console.log("handleChangeStatus : ", meta, file, status);
    }
    return (
        <Fragment>
            <Container fluid={true}>
                <Row>
                    <Col sm="12">
                        <Card>
                            <CardHeader>
                                <h5>Upload related files & attachments</h5>
                            </CardHeader>
                            <CardBody>
                                <Form>
                                    <div className="dz-message needsclick">
                                        <Dropzone
                                            getUploadParams={getUploadParams}
                                            onChangeStatus={handleChangeStatus}
                                            maxFiles={1}
                                            multiple={false}
                                            canCancel={false}
                                            inputContent="Drop a file OR select to browse local data"
                                            styles={{
                                                dropzone: { height: 325 },
                                                dropzoneActive: { borderColor: 'green' },
                                            }}
                                        />
                                    </div>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}

export default PageTwoMainHelper;
