import React, { Fragment, useState, useEffect } from 'react';
import "./styles.css";
import { Container, Card, CardHeader, CardBody, CardFooter, Row, Col } from "reactstrap";
import Slider from "react-slick";
import Breadcrumb from '../../../../../layout/breadcrumb';
import ReactPlayer from "react-player";
import moment from "moment";


const settings = {
    dots: true,
    infinite: true,
    draggable: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
        {
          breakpoint: 1350,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 675,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
};

const randomDate = (start, end) => {
    return new Date(start.getTime() + Math.random() * (end.getTime() - start.getTime()));
}

const ViewAllTutorialCoursesHelper = (props) => {

    const [ data, setData ] = useState({
        dataChunkOne: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "JeremyAlexanderBlong",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkTwo: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "TheHackingStreamer1092",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkThree: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "MrBeastSucks",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkFour: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "AdamJewler19",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkFive: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "JohnnyKnox2948",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        })
    });
    return (
        <Fragment>
            <Breadcrumb parent="View FREE Tutorial Video(s)" title="Learn New Hacking Skills Today.." />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className='shadow'>
                            <CardBody>
                                <Row>
                                    <Slider {...settings}>
                                        {typeof data.dataChunkOne !== "undefined" && data.dataChunkOne.length > 0 ? data.dataChunkOne.map((element, index) => {
                                            return (
                                                <Fragment key={index}>
                                                    <Col className='custom-card-chunk-tutorial' xl="3" sm="12" lg="3" md="3">
                                                        <Card className="card-tutorial-video shadow">
