                                        {typeof searchText !== "undefined" && searchText.length > 0 ? <InputGroupAddon onClick={() => {
                                            setSearchText("");
                                            setSearching(false);
                                        }} className={"searching-addon-list-people-right"} addonType="append">
                                            <i class="fa fa-times-circle fa-2x" aria-hidden="true"></i>
