import React, { Fragment, useState, useRef, useEffect, useMemo } from 'react';
import "./styles.css";
import Breadcrumb from '../../../../../../../../layout/breadcrumb';
import { Container, Row, Col, Card, CardBody, CardHeader, Button, Media } from "reactstrap";
import Slider from 'react-slick';
import { Truck, Gift,CreditCard,Clock } from 'react-feather';
import axios from "axios";
import { useParams } from "react-router-dom";
import { NotificationManager } from 'react-notifications';
import { connect } from "react-redux";
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import LoadingBar from 'react-top-loading-bar';
import _ from "lodash";


const ViewPreviouslySubmittedDataHackerAccountHelper = ({ userData }) => {

    const { id } = useParams();

    const slider1 = useRef();
    const slider2 = useRef();
    const [ data, setData ] = useState(null);
    const [ selected, setSelected ] = useState(null);
    const [ scrollable, setScrollable ] = useState(0);
    const [ showProgress, setShowProgress ] = useState(true);

    const [ state, setState ] = useState({ nav1: null, nav2: null });

    useEffect(() => {
        setState({
            nav1: slider1.current,
            nav2: slider2.current
