import React, { Fragment, useState, useEffect } from 'react';
import { Row, Col, Card, CardHeader, CardFooter, Container, CardBody, Media, Button } from 'reactstrap';
import moment from "moment";
import helpers from "./helpers/followingRelated/following/helpers.js";
import _ from "lodash";
import { useHistory } from "react-router-dom";

const { renderProfilePicVideo, redirectToHackersProfile } = helpers;

const FriendsTab = ({ user }) => {

    const history = useHistory();

    const [ cards, setCards ] = useState([]);


    const redirectToHackersProfile = (hackerID) => {
        console.log("redirectToHackersProfile ran...");

        history.push(`/hacker/profile/individual/view/${hackerID}`);
    }

    useEffect(() => {
        setCards([...user.followingHackers, ...user.followingCompanies]);
    }, [])

    return (
        <Fragment>
            <Row>
                <Container fluid={true}>
                    <Row>
                        <Col sm="12" md="12" lg="12" xl="12">
                            <Card className='shadow'>
                                <CardHeader className='b-l-primary b-r-primary'>
                                    <h3 className='text-left'>These are the people that this user is 'following' & actively keeping-up-with!</h3>
                                </CardHeader>
                                <CardBody>
                                    {cards.map((element, i) => {
                                        const profilePicture = _.has(element, "latestProfilePicVideo") ? `${process.env.REACT_APP_ASSET_LINK}/${element.latestProfilePicVideo.link}` : require(`../../../../../assets/images/avtar/4.jpg`);

                                        if (element.accountType === "hackers") {
                                            return (
                                                <figure className="col-xl-3 col-sm-6"><a href={null} data-size="1600x950">
                                                    <Media
                                                        src={profilePicture}
                                                        alt="Gallery"
                                                        className="img-thumbnail"
                                                    />
                                                    <div className="caption">
                                                        <h4>{element.followingFullName}</h4>
                                                        <p>Account Type: {element.followingAccountType}</p>
                                                        <p>Job Title: {element.followingJobTitle !== "undefined" ? element.followingJobTitle : "Not Provided.."}</p>
                                                        <p>Started Following: {moment(element.date).fromNow()}</p>
                                                    </div></a>
                                                </figure>
                                            );
                                        } else {
                                            return (
                                                <Col md="6" lg="6" xl="4" className="box-col-6" key={i}>
                                                    <Card className="custom-card">
                                                        <CardHeader className="card-header-banner-custom">
                                                            <Media body className="img-fluid banner-banner-custom" src={bannerImage} alt="banner-image-display" />
                                                        </CardHeader>
                                                        <div className="card-profile customized-card-profile">
                                                            {renderProfilePicVideo(profilePicture)}
                                                        </div>
                                                        <ul className="card-social">
                                                            <li><a href={null}><i className="fa fa-facebook"></i></a></li>
                                                            <li><a href={null}><i className="fa fa-google-plus"></i></a></li>
                                                            <li><a href={null}><i className="fa fa-twitter"></i></a></li>
                                                            <li><a href={null}><i className="fa fa-instagram"></i></a></li>
                                                            <li><a href={null}><i className="fa fa-rss"></i></a></li>
                                                        </ul>
                                                        <div className="text-center profile-details">
                                                            <h4>{`${element.firstName} ${element.lastName}`}</h4>
                                                            <h6>{element.fullyVerified === true ? "FULLY-VERIFIED!" : "Un-Verified."}</h6>
                                                            <hr />
                                                            <Button onClick={() => redirectToHackersProfile(element.uniqueId)} style={{ width: "90%" }} className="btn-pill btn-air-info" outline color="info-2x">View/Visit "Hacker/Contractor" Profile</Button>
                                                            <hr />
                                                        </div>
                                                        <CardFooter className="row">
                                                            <Col sm="4 col-4">
                                                                <h6>Successful/Completed Jobs</h6>
                                                                <h3 className="counter">{element.completedJobs} Jobs/Gigs Completed</h3>
                                                            </Col>
                                                            {element.reviews.length > 0 ? <Col sm="4 col-4">
                                                                <h6>Review Count</h6>
                                                                <h3><span className="counter">{element.reviews.length} Reviews (Total)</span></h3>
                                                            </Col> : <Col sm="4 col-4">
                                                                <h6>Experience</h6>
                                                                <h3><span className="counter">{element.points} XP-Experience Points</span></h3>
                                                            </Col>}
                                                            <Col sm="4 col-4">
                                                                <h6>Registration</h6>
                                                                <h3><span className="counter">Registered {moment(element.registrationDate).fromNow()}</span></h3>
                                                            </Col>
                                                        </CardFooter>
                                                    </Card>
                                                </Col>
                                            );
                                        }
                                    })}
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </Row>
        </Fragment>
    );
};

export default FriendsTab;
