import React, { Fragment,useEffect,useState } from 'react';
import Breadcrumb from '../../../../../../layout/breadcrumb'
import { Container, Row, Col, Card, CardHeader, CardBody, CardFooter, Media, Form, FormGroup, Label, Input, Button } from 'reactstrap'
import axios from 'axios';
import { connect } from "react-redux";
import { MyProfile,Bio,MarkJecno,Designer,Password,Website,Save,EditProfile,Company,Username,UsersCountryMenu,AboutMe,UpdateProfile,UsersTableTitle,FirstName,LastName,Address,EmailAddress,PostalCode,Country, UsersTableHeader,City,Edit,Update,Delete} from '../../../../../../constant';
import Select from 'react-select';
import DatePicker from "react-datepicker";
import countryList from "./helpers/countryList/countries.js";
import SlidingPane from "react-sliding-pane";
import Dropzone from 'react-dropzone-uploader';
import LoadingBar from 'react-top-loading-bar'
import {NotificationManager} from 'react-notifications';
import _ from "lodash";
import AvatarEditor from 'react-avatar-editor';
import "./styles.css";

const options = [
    { value: 'male', label: 'Male' },
    { value: 'female', label: 'Female' },
    { value: 'transgender', label: 'Transgender' },
    { value: "nonbinary", label: "Nonbinary" },
    { value: "genderqueer", label: "Genderqueer" },
    { value: "agender", label: "Agender" }
];
const yearOptions = [
    { value: "1", label: "1 Year of experience" },
    { value: "2", label: "2 Years of experience" },
    { value: "3", label: "3 Years of experience" },
    { value: "4", label: "4 Years of experience" },
    { value: "5", label: "5 Years of experience" },
    { value: "6", label: "6 Years of experience" },
    { value: "7", label: "7 Years of experience" },
    { value: "8", label: "8 Years of experience" },
    { value: "9", label: "9 Years of experience" },
    { value: "10", label: "10 Years of experience" },
    { value: "11-15", label: "11-15 Years of experience" },
    { value: "16-20", label: "16-20 Years of experience" },
    { value: "21-25", label: "21-25 Years of experience" },
    { value: "26-30", label: "26-30 Years of experience" },
    { value: "30+", label: "30+ Years of experience" },

];


const GeneralSettingsHelper = ({ userData }) => {
    const [ yearsOfExperience, setYearsOfExperience ] = useState(null);
    const [birthdate, setBirthDate] = useState(new Date());
    const [ file, setFile ] = useState(null);
    const [ values, setValues ] = useState({});
    const [ data, setData] = useState([]);
    const [ selectedOption, setSelectedOption ] = useState(null);
    const [ progress, setProgress ] = useState(0);
    const [ personal, setPersonalData ] = useState({});

    const [pane, setPane] = useState({
        isPaneOpen: false,
        isPaneOpenLeft: false,
    });
    useEffect(() => {
        console.log("mounted.", userData.uniqueId);

        axios.get(`http://localhost:5000/gather/general/user/data`, {
            params: {
                id: userData.uniqueId,
                accountType: userData.accountType
            }
        }).then((res) => {
            if (res.data.message === "Gathered user!") {
                console.log(res.data);

                const { user } = res.data;

                setPersonalData(user);
            } else {
                console.log("err", res.data);
            }
        }).catch((err) => {
            console.log(err);
        })
    }, []);
    const handleChangeExp = (selected) => {
        setYearsOfExperience(selected);
    }
    const handleChange = (selected) => {
        setSelectedOption(selected);
    }
    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setValues(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        }); 
    }
    const getUploadParams = ({ meta }) => { return { url: 'https://httpbin.org/post' } };

    const handleChangeStatus = ({ meta, file }, status) => {
        console.log("handleChangeStatus ran. - :", meta, file, status);

        setFile(file);
    }
    const handleProfilePicUpload = () => {
        console.log("handleProfilePicUpload clicked.");

        setPane(prevState => {
            return {
                ...prevState,
                isPaneOpen: false
            }
        });

        NotificationManager.info(`We've started uploading your new profile picture/video!`, "Uploading content!", 4500);

        const data = new FormData();

        data.append("file", file);
        data.append("uniqueId", userData.uniqueId);

