            onUploadProgress: function(progressEvent) {
              let percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);

              console.log("percentCompleted", percentCompleted);

              setProgress(percentCompleted);
            },
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        };

        axios.post(`http://localhost:5000/upload/profile/picture/video`, data, config).then((res) => {
            if (res.data.message === "Successfully uploaded content!") {
                console.log(res.data);



                NotificationManager.success('Successfully uploaded your data! Your new profile picture/video is now live.', 'Successfully uploaded data!', 4500);
            } else {
