import React, { Fragment } from "react";
import ReactPlayer from "react-player";
import _ from "lodash";
import { Media } from 'reactstrap';


const renderPictureOrVideoLast = (file) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li>
                        <div className="latest-post">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li>
                        <div className="latest-post">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li>
                    <div className="latest-post">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
export default {
    renderPictureOrVideoLast
};
