import React, { Component, Fragment } from 'react';
import axios from "axios";
import places from "places.js";
import { Input } from "reactstrap";

class LocationSearchInput extends Component {
constructor(props) {
    super(props);

    this.state = { address: '' };
}

    handleChange = e => {
        const { value } = e.target;

        this.setState({
            address: value
        }, () => {
            axios.get(`https://api.tomtom.com/search/2/autocomplete/${this.state.address}.json?key=${process.env.REACT_APP_TOMTOM_API_KEY}&language=en-US&limit=10`).then((res) => {
                console.log("Response from tomtom: ", res.data);
            }).catch((err) => {
                console.log("tomtom err: ", err);
            })
        })
    };
    render() {
        const { address } = this.state;

        const placesAutocomplete = places({
            appId: process.env.REACT_APP_ALGOLIA_APP_ID,
            apiKey: process.env.REACT_APP_ALGOLIA_API_KEY,
            container: document.getElementById('#address-input')
        });
        return (
        <Fragment>
            <Input id="address-input" className="form-control" id="exampleFormControlInput6" type="text" placeholder="Enter your business address (address given to hackers)" />
        </Fragment>
        );
    }
}
export default LocationSearchInput;
