import React, { Fragment } from 'react';

const handleFileDeletionUploadedFiles = (i, setMyFile, setPopoverStates, selected) => {
    setMyFile(prevState => {
        return prevState.filter((item, idx) => {
            if (item.id !== selected.id) {
                return true;
            }
        })
    });
    setPopoverStates(prevState => {
        return {
            ...prevState,
            [`popover${i}`]: false
        }
    });
}
const handleDotsClickExpansion = (file, index) => {
    setPopoverStates(prevState => {
        return {
            ...prevState,
            [`popover${index}`]: true
        }
    });
}
const calculateFileType = (type) => {
    switch (type) {
        case "video/mp4":
            return "mp4";
            break;
        case "image/png":
            return "png";
            break;
        case "image/jpeg":
            return "jpeg";
            break;
        case "image/jpg":
            return "jpg";
            break;
        case "image/gif":
            return "gif";
            break;
        case "image/bmp":
            return "bmp";
            break;
        case "application/pdf":
            return "pdf";
            break;
        case "text/csv":
            return "csv";
            break;
        case "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet":
            return "xlsx";
            break;
        case "application/vnd.openxmlformats-officedocument.wordprocessingml.document":
            return "docx";
            break;
        case "video/webm":
            return "webm";
            break;
        case "audio/mpeg":
            return "mp3";
            break;
        default:
            break;
    }
}

export default {
    handleFileDeletionUploadedFiles,
    handleDotsClickExpansion,
    calculateFileType
}
