import React, { Fragment } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { CardBody, CardHeader, Card, Col, Container, Row } from "reactstrap";
import "./styles.css";
import Select from "react-dropdown-select";

const options = [{
    label: "one",
    value: "one"
}, {
    label: "two",
    value: "two"
}, {
    label: "three",
    value: "three"
}, {
    label: "four",
    value: "four"
}, {
    label: "five",
    value: "five"
}, {
    label: "six",
    value: "six"
}, {
    label: "seven",
    value: "seven"
}]

const CreateNewForumPostingHelper = ({ userData }) => {

    const customDropdownRenderer = ({ props, state, methods }) => {
        return (
          <div>
            <div className="search-and-toggle">
              <input
                type="text"
                // value={""}
                onChange={() => {}}
                placeholder="Search for a community.."
              />
            </div>
            <div className="items-items">
              {options.map((option, index) => {
                  return (
                    <div
                        className={"options-mapped-div"}
                        key={index}
                        onClick={option.disabled ? null : () => {}}>
                        <img src={require(`../../../../assets/icons/reddit-${Math.floor(Math.random() * (6 - 1 + 1) + 1)}.png`)} className={"community-icon-forum"} />
                    </div>
                  );
                })}
            </div>
          </div>
        );
    };
    return (
        <Fragment>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card style={{ marginTop: "50px" }}>
                            <CardHeader className="b-l-info b-r-info">
                                <h3>Create a new forum post (Must be associated with a <a className="create-forum-post-a" href={null}>community</a>)</h3>
                            </CardHeader>
                            <CardBody>
                                <Select dropdownRenderer={customDropdownRenderer} options={options} onChange={(values) => console.log(values)} />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(CreateNewForumPostingHelper);
