import React, { Fragment, useState, useEffect } from 'react';
import "./styles.css";
import { Row, Col, Media, Input, Label, Button, Form } from 'reactstrap';
import axios from 'axios';
import { connect } from "react-redux";
import { useForm, Controller } from 'react-hook-form';
import MainHooksCourseIndividualCustomHelpers from "./helpers/reduxFormHelper.js";
import ReactPlayer from "react-player";
import helpers from "./helpers/helperFunctions.js";
import { NotificationManager } from 'react-notifications';

const checkMessageMeetsCritera = MainHooksCourseIndividualCustomHelpers().checkMessageMeetsCritera;


const { RenderPopoverEmojiLogic } = helpers;

const CommentsIndividualCourseHelper = ({ courseData, userData }) => {

    const { register, handleSubmit, getValues, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
        // delayError: 500
    });

    const [ comments, setCommentsState ] = useState([]);
    const [ popover, setPopoverState ] = useState({}); 

    useEffect(() => {
        setCommentsState(courseData.comments);

        setPopoverState(() => {
            const newObjCount = {};
            const comments = courseData.comments;
            // reassign state items in preperation for future state changes...
            for (const keyyy in comments) {
                newObjCount[`comment${keyyy}`] = false;
            }
            setPopoverState(newObjCount);
        })
    }, []);

    console.log("popover state...:", popover);

    const currentValues = getValues();

    const onSubmitForm = (data) => {
        console.log("onSubmitForm submitted form properly...!:", data);

        const { comment } = data;

        const configuration = {
            id: userData.uniqueId,
            courseId: courseData.id,
            comment,
            firstName: userData.firstName,
            lastName: userData.lastName,
            accountType: userData.accountType,
            mostRecentProfilePicVideo: typeof userData.profilePicsVideos !== "undefined" && userData.profilePicsVideos.length > 0 ? userData.profilePicsVideos[userData.profilePicsVideos.length - 1] : null
        }

        axios.post(`${process.env.REACT_APP_BASE_URL}/post/comment/course/individual/listing`, configuration).then((res) => {
            if (res.data.message === "Successfully posted comment!") {
                console.log(res.data);

                const { updatedComments } = res.data;

                setValue("comment", "", { shouldValidate: false });

                setCommentsState(updatedComments);
            } else {
                console.log("Err", res.data);
            }
        }).catch((err) => {
            console.log(err);
        })
    }
    const handleRespondEmoji = (emojiName, comment) => {
        console.log("emojiName", emojiName);

        const configuration = {
            emojiName,
            id: userData.uniqueId,
            courseId: courseData.id,
            commentID: comment.id,
            comment
        };

        axios.post(`${process.env.REACT_APP_BASE_URL}/respond/emoji/comment/course/learning`, configuration).then((res) => {
            if (res.data.message === "Successfully reacted to comment!") {
                console.log(res.data);

                const { course } = res.data;

                NotificationManager.success("Successfully REACTED to the selected comment, Your response is now live!", "Successfully reacted to comment!", 4500);

                setCommentsState(course.comments);
            } else {
                console.log("Err", res.data);
            }
        }).catch((err) => {
            console.log(err);
        })
    }
    const renderFormErrors = (errors) => {
        console.log("renderFormErrors", errors);
    }
    const renderCommentPicOrVideo = (file) => {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <Media className="align-self-center">
                        <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"media-body align-self-center"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                    </Media> 
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <Media className="align-self-center">
                        <Media className="align-self-center" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} alt=""/>
                    </Media>
                </Fragment>
            );
        }  
    }
    const renderEmojiLogic = (reactions) => {
        const emojiArrayResponded = [];
        // loop over existing responses
        for (const key in reactions) {
            const element = reactions[key];
            if (element > 0) {
                emojiArrayResponded.push(key);
            }
        }
        console.log("emojiArrayResponded", emojiArrayResponded);
        emojiArrayResponded.map((reaction) => {
            switch (reaction) {
                case "partying":
                    return <img src={require("../../../../../../../assets/gifs/partying.gif")} className={"emoji-already-existant"} />;
                    break;
                case "screaming":
                    return <img src={require("../../../../../../../assets/gifs/screaming.gif")} className={"emoji-already-existant"} />;
                    break;
                case "steaming":
                    return <img src={require("../../../../../../../assets/gifs/steaming.gif")} className={"emoji-already-existant"} />;
                    break;
                case "sunglasses":
                    return <img src={require("../../../../../../../assets/gifs/sunglasses.gif")} className={"emoji-already-existant"} />;
                    break;
                case "tearsOfJoy":
                    return <img src={require("../../../../../../../assets/gifs/tearsOfJoy.gif")} className={"emoji-already-existant"} />;
                    break;
                case "vomitting":
                    console.log("MATCH!!!");
                    return <img src={require("../../../../../../../assets/gifs/vomitting.gif")} className={"emoji-already-existant"} />;
                    break;
                default:
                    break;
            }
        })
    }
    return (
        <Fragment>
            <section className="comment-box">
