import React, { Fragment, useState, useEffect } from "react";
import Sheet from 'react-modal-sheet';
import { Row, Col, Card, CardBody, Button, Container, CardHeader, Media, PopoverBody, PopoverHeader, InputGroupAddon, Input, InputGroup } from 'reactstrap';
import "./styles.css";
import helpers from "../misc/index.js";
import moment from "moment";
import ReactPlayer from 'react-player';
import { MoreVertical } from "react-feather";
import one from '../../../../../../../assets/images/job-search/1.jpg';
import two from '../../../../../../../assets/images/job-search/6.jpg';
import timeline1 from "../../../../../../../assets/images/social-app/timeline-1.png";


const { RenderPopoverEmojiLogicMainPostings, renderProfilePicVideoPost } = helpers;

// Main function for this file is at/near the very bottom of this file... it's called <ViewPostFileContentHelper />

const renderGridImageOrVideo = (file, bottom) => {
    if (file.type.includes("video")) {
        // video logic
        return (
            <Media className={bottom === true ? "maxed-out-post-video-custom-wrapper-bottom" : "maxed-out-post-video-custom-wrapper"}>
                <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} height={"100%"} className={"maxed-out-post-video-custom"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
            </Media>
        );
    } else if (file.type.includes("image")) {
        // image logic
        return (
            <Fragment>
                <div className={bottom === true ? "align-post-content-picture-bottom-item" : "align-post-content-picture"}>
                    <Media className="post-image-resized" alt="post-picture" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                </div>
            </Fragment>
        );
    } else {
        console.log("NEITHER MATCH.")
    }
}

const RenderMainFileContentHelper = ({ files, post }) => {
    switch (files.length) {
        case 0:
            return null;
            break;
        case 1:
            return (
                <Fragment>
                    <Media className="img-fluid" alt="" src={timeline1} />
                </Fragment>
            );
            break;
        case 2:
            return (
                <Fragment>
                    <Row>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                    </Row>
                </Fragment>
            );
            break;
        case 3:
            return (
                <Fragment>
                    <Row>
                        <Col className={"image-render-col-custom"} sm="12" md="12" lg="12" xl="12">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                    </Row>
                    <Row style={{ paddingTop: "3px" }}>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                    </Row>
                </Fragment>
            );
            break;
        case 4:
            return (
                <Fragment>
                    <Row>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                    </Row>
                    <Row style={{ paddingTop: "3px" }}>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            <Media className="img-fluid" alt="" src={timeline1} />
                        </Col>
                    </Row>
                </Fragment>
            );
            break;
        case 5:
            return (
                <Fragment>
                    <Row>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            {renderGridImageOrVideo(files[0], false)}
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="6" lg="6" xl="6">
                            {renderGridImageOrVideo(files[1], false)}
                        </Col>
                    </Row>
                    <Row style={{ paddingTop: "3px" }}>
                        <Col className={"image-render-col-custom"} sm="12" md="4" lg="4" xl="4">
                            {renderGridImageOrVideo(files[2], true)}
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="4" lg="4" xl="4">
                            {renderGridImageOrVideo(files[3], true)}
                        </Col>
                        <Col className={"image-render-col-custom"} sm="12" md="4" lg="4" xl="4">
                            {renderGridImageOrVideo(files[4], true)}
                        </Col>
                    </Row>
                </Fragment>
            );
            break;

        default:
            return null;
            break;
    }
}
const PopoverEmojiMainPostItemHelper = ({ userData, handleRespondEmoji, reactions, setPopoverState, popoverState, targetAndPopState }) => {

    const [stateCount, setStateCount] = useState(0);
    const [ready, setReady] = useState(false);

    useEffect(() => {
        let count = 0;
        // loop over reactions
        for (const key in reactions) {
            const reactionOutcome = reactions[key];

            // check if emoji is already added - if not... add it!
            if (reactionOutcome > 0) {
                count++;
            }
        }
        setStateCount(count);
        setReady(true);
    }, []);

    const renderMainContentPopover = () => {
        const closePopover = () => {
            setPopoverState(false);
        }
        if (ready === true) {
            return (
                <Fragment>
