            const { startDate, endDate, key } = selectedDate;

            const newData = {
                startDate: new Date(startDate),
                endDate: new Date(endDate),
                key
            }

            newDatesArray.push(newData);
        }

        this.setState({
            datesSelectable: newDatesArray,
            individual: listing,
            isOpen: true
        })
    }
