import React, { useState, useRef, useEffect, Fragment } from 'react';
import { useForm, Controller } from 'react-hook-form';


const HookHelpers = () => {

    const { register, handleSubmit, control, reset, getValues, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
    });

    const startBidCheck = {
        check: {...register("startBid", { required: false, minLength: {
            value: 15,
            message: "You must enter AT Least 15 characters"
        }, maxLength: {
            value: 75,
            message: "You may ONLY enter 75 characters or less"
        }, onBlur: (e) => {
            // check if valid url in helper file.

        }})},
        name: "startBid",
        placeholder: "Enter a starting bid...",
        type: "text",
        label: "Starting Bid"
    };

    return {
        startBidCheck
    }
}


export default {
    // function exports go here
    HookHelpers
};
