import React,{useState,useEffect, Fragment} from 'react';
import Breadcrumb from '../../../../layout/breadcrumb'
import { Container, Row, Col, Card, CardBody, Media, Form, FormGroup, Input, InputGroup, InputGroupAddon, Button, Nav, NavItem, NavLink, TabPane, TabContent } from 'reactstrap'
import four from '../../../../assets/images/user/4.jpg';
import one from '../../../../assets/images/user/1.jpg';
import two from '../../../../assets/images/user/2.png';
import errorImg from '../../../../assets/images/search-not-found.png';
import start_conversion from '../../../../assets/images/start-conversion.jpg';
import {Picker} from 'emoji-mart'
import { CALL, STATUS, PROFILE, EricaHughes, VincentPorter, Active, ChataApp_p1, ChataApp_p2, Following, Follower, MarkJecno, Send } from '../../../../constant';
import { connect } from "react-redux";
import moment from "moment";
import "./styles.css";



const MessagingMainHelper = ({ SBData, userData }) =>  {

    const allMembers = [];
    const chats = [];
    const selectedUser = [];
    const currentUser = [];
    const online = [];
    const members = [];
    const [ searchKeyword, setSearchKeyword ] = useState('');
    const [ messageInput, setMessageInput ] = useState('');
    const [ showEmojiPicker,setShowEmojiPicker ] = useState(false)
    const [ menuToggle, setMenuToggle ] = useState(false);
    const [ activeTab, setActiveTab ] = useState('1');
    const [ groupChannels, setGroupChannels ] = useState([]);
    const [ messageList, setMessageListState ] = useState([]);
    const [ channel, setChannelState ] = useState(null);

    useEffect(() => {
        console.log("SBData messagingMain.js ---- : ", SBData);
        const listQuery = SBData.OpenChannel.createOpenChannelListQuery();
        listQuery.includeEmpty = true;
        listQuery.memberStateFilter = 'all';    // 'all', 'joined_only', 'invited_only', 'invited_by_friend', and 'invited_by_non_friend'
        listQuery.order = 'latest_last_message';    // 'chronological', 'latest_last_message', 'channel_name_alphabetical', and 'metadata_value_alphabetical'
        listQuery.limit = 20;   // The value of pagination limit could be set up to 100.

        if (listQuery.hasNext) {
            listQuery.next((groupChannels, error) => {
